package Bai1;

public class Bai10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x = 2;
    int n = 5;
    int luythua = 1;
    for (int i = 1; i<= n; i++) {
    	luythua *= x;
    }
    System.out.println(" T(x,n) =" + luythua); // Tinh T(x,n) = x^n
}}
